package javaProject;

public class Condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 50;
		
		int number = 0;
		
		if(a>c) {
			number = a;
		} 
		else  {
			number = c;
		}
		if (b>c) {
			number = b;
		}
		
		System.out.print(number);

	}
	
}
